import React, { useState } from 'react';
import { View, Text, TextInput, StyleSheet, TouchableOpacity } from 'react-native';
import { colors } from '../../utils/colors';
import { validateInvite } from '../../services/ai';

const isDev = process.env.NODE_ENV === 'development';

export default function Landing({ onValidInvite }) {
  const [inviteCode, setInviteCode] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    try {
      const result = await validateInvite(inviteCode);
      if (result.valid) {
        onValidInvite(result.userName);
      } else {
        setError('Invalid invite code');
      }
    } catch (error) {
      setError('Error validating invite code');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Welcome to HustleBot</Text>
      <TextInput
        style={styles.input}
        value={inviteCode}
        onChangeText={setInviteCode}
        placeholder="Enter invite code"
        placeholderTextColor={colors.text.tertiary}
      />
      {isDev && (
        <Text style={styles.devNote}>
          Dev mode: Use code "TEST123"
        </Text>
      )}
      {error ? <Text style={styles.error}>{error}</Text> : null}
      <TouchableOpacity style={styles.button} onPress={handleSubmit}>
        <Text style={styles.buttonText}>Enter</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 30,
    color: colors.text.primary
  },
  input: {
    width: '100%',
    padding: 15,
    borderRadius: 10,
    backgroundColor: colors.background.secondary,
    marginBottom: 10,
    color: colors.text.primary
  },
  error: {
    color: colors.danger,
    marginBottom: 10
  },
  button: {
    backgroundColor: colors.primary,
    padding: 15,
    borderRadius: 10,
    width: '100%',
    alignItems: 'center'
  },
  buttonText: {
    color: colors.text.inverse,
    fontWeight: 'bold'
  },
  devNote: {
    color: colors.info,
    marginTop: 5,
    marginBottom: 10,
    fontSize: 12
  }
});
