import { getFirestore, collection, addDoc, serverTimestamp } from 'firebase/firestore';

export const generateReferralCode = async (userId) => {
  try {
    console.log('Generating referral code for user:', userId);
    const db = getFirestore();

    // Generate a unique code
    const code = generateUniqueCode();
    console.log('Generated unique code:', code);

    // Save to Firestore with expiration
    await addDoc(collection(db, 'referralCodes'), {
      code,
      createdBy: userId,
      createdAt: serverTimestamp(),
      expiresAt: new Date(Date.now() + 48 * 60 * 60 * 1000), // 48 hours
      used: false,
      usedBy: null
    });
    console.log('Saved referral code to Firestore');

    return code;
  } catch (error) {
    console.error('Error generating referral code:', error);
    throw error;
  }
};

function generateUniqueCode() {
  // Generate a code format: XXXX-XXXX-XXXX
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  const segments = 3;
  const segmentLength = 4;

  let code = '';
  for (let i = 0; i < segments; i++) {
    for (let j = 0; j < segmentLength; j++) {
      code += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    if (i < segments - 1) code += '-';
  }

  return code;
}
