import React, { useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { colors } from '../../utils/colors';
import ChatUI from '../../components/Chat/ChatUI';
import Navbar from '../../components/Navbar';
import { getUrlParams } from '../../utils/urlUtils';

export default function LandingPage() {
  const { ref, questions, refOffer } = getUrlParams();
  const [showChat, setShowChat] = useState(false);
  console.log('Landing page params:', { ref, questions, refOffer });

  return (
    <View style={styles.container}>
      <Navbar onGetStarted={() => setShowChat(true)} />
      <View style={styles.content}>
        {showChat ? (
          <ChatUI 
            questionsBeforeLogin={questions} 
            showRefOffer={refOffer}
          />
        ) : (
          <View style={styles.hero}>
            <Text style={styles.title}>Welcome to HustleBot</Text>
            <Text style={styles.subtitle}>
              Your AI companion for finding your passion and achieving your goals
            </Text>
          </View>
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background.primary
  },
  content: {
    flex: 1,
    marginTop: 64 // Height of navbar
  },
  hero: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 24
  },
  title: {
    fontSize: 48,
    fontWeight: 'bold',
    color: colors.text.primary,
    marginBottom: 24,
    textAlign: 'center',
    '@media (max-width: 768px)': {
      fontSize: 36
    }
  },
  subtitle: {
    fontSize: 24,
    color: colors.text.secondary,
    textAlign: 'center',
    maxWidth: 600,
    lineHeight: 32,
    '@media (max-width: 768px)': {
      fontSize: 18,
      lineHeight: 24
    }
  }
});
