import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { colors } from '../../utils/colors';
import ChatUI from '../../components/Chat/ChatUI';
import { ChatIcon, GrowthIcon, SecureIcon } from '../../assets/icons';

export default function LandingPage() {
  const [showChat, setShowChat] = React.useState(false);

  if (showChat) {
    return <ChatUI />;
  }

  return (
    <View style={styles.container}>
      {/* Hero Section */}
      <View style={styles.hero}>
        <View style={styles.heroContent}>
          <Text style={styles.heroTitle}>
            Your Personal Growth Journey Starts Here
          </Text>
          <Text style={styles.heroSubtitle}>
            Discover your potential through meaningful conversations
          </Text>
          <TouchableOpacity 
            style={styles.ctaButton}
            onPress={() => setShowChat(true)}
          >
            <Text style={styles.ctaButtonText}>Start Your Journey</Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Features Section */}
      <View style={styles.features}>
        <View style={styles.feature}>
          <ChatIcon
            style={styles.featureIcon}
          />
          <Text style={styles.featureTitle}>AI-Powered Conversations</Text>
          <Text style={styles.featureText}>
            Engage in meaningful dialogue that adapts to your needs
          </Text>
        </View>
        <View style={styles.feature}>
          <GrowthIcon
            style={styles.featureIcon}
          />
          <Text style={styles.featureTitle}>Personal Growth</Text>
          <Text style={styles.featureText}>
            Track your progress and discover new insights
          </Text>
        </View>
        <View style={styles.feature}>
          <SecureIcon
            style={styles.featureIcon}
          />
          <Text style={styles.featureTitle}>Secure & Private</Text>
          <Text style={styles.featureText}>
            Your conversations are protected and confidential
          </Text>
        </View>
      </View>

      {/* Footer */}
      <View style={styles.footer}>
        <Text style={styles.footerText}>Â© 2024 Personal Growth AI</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background.primary
  },
  hero: {
    height: 600,
    backgroundColor: colors.background.secondary,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20
  },
  heroContent: {
    maxWidth: 800,
    alignItems: 'center'
  },
  heroTitle: {
    fontSize: 48,
    fontWeight: 'bold',
    color: colors.text.primary,
    textAlign: 'center',
    marginBottom: 20
  },
  heroSubtitle: {
    fontSize: 24,
    color: colors.text.secondary,
    textAlign: 'center',
    marginBottom: 40
  },
  ctaButton: {
    backgroundColor: colors.primary,
    paddingHorizontal: 40,
    paddingVertical: 20,
    borderRadius: 30,
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84
  },
  ctaButtonText: {
    color: colors.text.inverse,
    fontSize: 20,
    fontWeight: 'bold'
  },
  features: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 60,
    backgroundColor: colors.background.primary
  },
  feature: {
    flex: 1,
    maxWidth: 300,
    alignItems: 'center',
    padding: 20
  },
  featureIcon: {
    width: 64,
    height: 64,
    marginBottom: 20
  },
  featureTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: colors.text.primary,
    marginBottom: 10,
    textAlign: 'center'
  },
  featureText: {
    fontSize: 16,
    color: colors.text.secondary,
    textAlign: 'center',
    lineHeight: 24
  },
  footer: {
    padding: 20,
    backgroundColor: colors.background.tertiary,
    alignItems: 'center'
  },
  footerText: {
    color: colors.text.tertiary
  }
});
