import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { colors } from '../../utils/colors';
import ChatUI from '../../components/Chat/ChatUI';
import Navbar from '../../components/Navbar';
import { getUrlParams } from '../../utils/urlUtils';

export default function LandingPage() {
  const { ref, questions, refOffer } = getUrlParams();
  console.log('Landing page params:', { ref, questions, refOffer });

  return (
    <View style={styles.container}>
      <Navbar />

      <View id="about-section" style={styles.section}>
        <Text style={styles.sectionTitle}>About HustleBot</Text>
        <Text style={styles.sectionText}>
          HustleBot is your AI-powered companion on the journey to finding your passion 
          and achieving your goals. Through meaningful conversations and personalized 
          guidance, we help you discover what drives you and connect you with others 
          who share your ambitions.
        </Text>
      </View>

      <View id="features-section" style={styles.section}>
        <Text style={styles.sectionTitle}>Features</Text>
        <View style={styles.featureGrid}>
          <View style={styles.featureCard}>
            <Text style={styles.featureTitle}>AI-Powered Conversations</Text>
            <Text style={styles.featureText}>
              Engage in meaningful dialogue with our advanced AI to explore your interests and goals.
            </Text>
          </View>
          <View style={styles.featureCard}>
            <Text style={styles.featureTitle}>Personalized Guidance</Text>
            <Text style={styles.featureText}>
              Receive tailored advice and actionable steps to help you progress on your journey.
            </Text>
          </View>
          <View style={styles.featureCard}>
            <Text style={styles.featureTitle}>Community Connection</Text>
            <Text style={styles.featureText}>
              Connect with like-minded individuals through our referral system.
            </Text>
          </View>
        </View>
      </View>

      <View id="chat-section" style={styles.chatSection}>
        <ChatUI 
          questionsBeforeLogin={questions} 
          showRefOffer={refOffer}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background.primary
  },
  section: {
    padding: 64,
    marginTop: 64,
    '@media (max-width: 768px)': {
      padding: 32,
      marginTop: 32
    }
  },
  sectionTitle: {
    fontSize: 36,
    fontWeight: 'bold',
    color: colors.text.primary,
    marginBottom: 24,
    textAlign: 'center',
    '@media (max-width: 768px)': {
      fontSize: 28
    }
  },
  sectionText: {
    fontSize: 18,
    color: colors.text.secondary,
    textAlign: 'center',
    maxWidth: 800,
    marginHorizontal: 'auto',
    lineHeight: 28,
    '@media (max-width: 768px)': {
      fontSize: 16,
      lineHeight: 24
    }
  },
  featureGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
    gap: 32,
    maxWidth: 1200,
    marginHorizontal: 'auto',
    marginTop: 48
  },
  featureCard: {
    backgroundColor: colors.background.secondary,
    padding: 32,
    borderRadius: 16,
    borderWidth: 1,
    borderColor: colors.background.tertiary
  },
  featureTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: colors.text.primary,
    marginBottom: 16,
    '@media (max-width: 768px)': {
      fontSize: 20
    }
  },
  featureText: {
    fontSize: 16,
    color: colors.text.secondary,
    lineHeight: 24
  },
  chatSection: {
    flex: 1,
    marginTop: 64,
    '@media (max-width: 768px)': {
      marginTop: 32
    }
  }
});
