import React from 'react';
import ErrorBoundary from './src/components/ErrorBoundary';
import Landing from './src/screens/Landing';
import ChatUI from './src/components/Chat/ChatUI';
import { clearInviteFromURL } from './src/utils/urlUtils';

export default function App() {
  const [userName, setUserName] = React.useState(null);

  const handleValidInvite = (name) => {
    setUserName(name);
    clearInviteFromURL(); // Clean up URL after successful invite
  };

  return (
    <ErrorBoundary>
      {userName ? (
        <ChatUI userName={userName} />
      ) : (
        <Landing onValidInvite={handleValidInvite} />
      )}
    </ErrorBoundary>
  );
}
