import React, { useState, useRef, useEffect } from 'react';
import { View, Text, TextInput, StyleSheet, TouchableOpacity, FlatList, ActivityIndicator } from 'react-native';
import { colors } from '../../utils/colors';
import { sendMessage } from '../../services/ai';
import GoogleSignIn from '../Auth/GoogleSignIn';

export default function ChatUI() {
  const isDev = process.env.NODE_ENV === 'development';
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [connectionError, setConnectionError] = useState(false);
  const [questionCount, setQuestionCount] = useState(0);
  const [showLoginPrompt, setShowLoginPrompt] = useState(false);
  const [user, setUser] = useState(null);
  const listRef = useRef(null);

  // Configure when to show login prompt based on environment
  const LOGIN_PROMPT_THRESHOLD = isDev ? 1 : 5;

  useEffect(() => {
    // Add welcome message with initial question
    const welcomeMessage = {
      id: Date.now(),
      text: `Welcome! I'm here to help you achieve your goals. What brings you here today?`,
      sender: 'bot',
      isQuestion: true
    };
    setMessages([welcomeMessage]);
  }, []);

  useEffect(() => {
    if (isDev) {
      console.log('Development mode: Chat initialized');
    }
  }, []);

  // Watch question count and show login prompt at threshold
  useEffect(() => {
    if (questionCount >= LOGIN_PROMPT_THRESHOLD && !showLoginPrompt && !user) {
      console.log(`Showing login prompt after ${questionCount} questions`);
      setShowLoginPrompt(true);
    }
  }, [questionCount, showLoginPrompt, user]);

  useEffect(() => {
    // Check backend connection
    const checkConnection = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/health');
        if (!response.ok) throw new Error('Backend unavailable');
        setConnectionError(false);
      } catch (error) {
        console.error('Connection check failed:', error);
        setConnectionError(true);
      }
    };

    checkConnection();
    const interval = setInterval(checkConnection, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleSendMessage = async () => {
    if (!input.trim() || loading) return;

    try {
      if (connectionError) {
        throw new Error('Cannot connect to chat service');
      }

      setLoading(true);
      const userMessage = { id: Date.now(), text: input, sender: 'user' };
      setMessages(prev => [...prev, userMessage]);
      setInput('');

      const result = await sendMessage(input);

      // Handle various error cases
      if (!result) {
        throw new Error('No response received from server');
      }

      if (!result.text && !result.options) {
        // Get a fallback question when we get an empty response
        const fallbackQuestion = {
          text: "I didn't quite catch that. Let me ask you something else - what motivates you the most?",
          options: ["Personal growth", "Making a difference", "Success and achievement"]
        };
        result.text = fallbackQuestion.text;
        result.options = fallbackQuestion.options;
      }

      const botMessage = {
        id: Date.now(),
        text: result.text || "I'm having trouble understanding. Could you rephrase that?",
        sender: 'bot',
        isQuestion: typeof result.text === 'string' && 
          (result.text.includes('?') || result.text.includes("I'd like to understand")),
        options: result.options || []
      };

      setMessages(prev => [...prev, botMessage]);

      // Increment question count if this was a question
      if (botMessage.isQuestion) {
        const newCount = questionCount + 1;
        setQuestionCount(newCount);
      }
    } catch (error) {
      console.error('Chat error:', error);
      // Handle specific error types
      let errorMessage;
      if (connectionError) {
        errorMessage = 'Cannot connect to chat service. Please check your connection.';
      } else if (error.message.includes('Empty response') || error.message.includes('No response')) {
        errorMessage = "I'm having trouble processing that right now. Let's try a different approach - what interests you most about self-improvement?";
      } else {
        errorMessage = "I apologize for the confusion. Let's try a different approach - what interests you most about self-improvement?";
      }

      setMessages(prev => [...prev, {
        id: Date.now(),
        text: errorMessage,
        sender: 'bot',
        isQuestion: true,
        options: [
          "Learning and growth",
          "Setting and achieving goals",
          "Building better habits"
        ]
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleQuickResponse = async (response) => {
    if (loading) return;

    try {
      setLoading(true);
      const userMessage = { id: Date.now(), text: response, sender: 'user' };
      setMessages(prev => [...prev, userMessage]);

      const result = await sendMessage(response);

      // Handle various error cases
      if (!result) {
        throw new Error('No response received from server');
      }

      if (!result.text && !result.options) {
        // Get a fallback question when we get an empty response
        const fallbackQuestion = {
          text: "That's interesting. Let me ask you something else - how do you define success?",
          options: ["Personal fulfillment", "Professional achievements", "Life balance"]
        };
        result.text = fallbackQuestion.text;
        result.options = fallbackQuestion.options;
      }

      const botMessage = {
        id: Date.now(),
        text: result.text || "I understand. Tell me more about that.",
        sender: 'bot',
        isQuestion: typeof result.text === 'string' && 
          (result.text.includes('?') || result.text.includes("I'd like to understand")),
        options: result.options || []
      };

      setMessages(prev => [...prev, botMessage]);

      // Increment question count and check for login prompt
      if (botMessage.isQuestion) {
        const newCount = questionCount + 1;
        setQuestionCount(newCount);
      }
    } catch (error) {
      console.error('Quick response error:', error);
      // Handle specific error types with engaging fallback questions
      let errorMessage;
      let errorOptions;

      if (connectionError) {
        errorMessage = 'Cannot connect to chat service. Please check your connection.';
        errorOptions = [];
      } else if (error.message.includes('Empty response') || error.message.includes('No response')) {
        errorMessage = "I'd love to explore that further. What aspect of it interests you most?";
        errorOptions = ["The challenge", "The opportunity", "The potential impact"];
      } else {
        errorMessage = "That's an interesting perspective. Could you elaborate on what matters most to you?";
        errorOptions = ["Personal development", "Achieving goals", "Making a difference"];
      }

      setMessages(prev => [...prev, {
        id: Date.now(),
        text: errorMessage,
        sender: 'bot',
        isQuestion: true,
        options: errorOptions
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    // TODO: Implement Google OAuth
    console.log('Google login clicked');
  };

  const handleGoogleSignIn = (userData) => {
    setUser(userData);
    setShowLoginPrompt(false);
    console.log('User signed in:', userData);
    // TODO: Save conversation history
  };

  const handleSkipSignIn = () => {
    setShowLoginPrompt(false);
    console.log('User skipped sign in');
  };

  return (
    <View style={styles.container}>
      {isDev && (
        <View style={styles.devBanner}>
          <Text style={styles.devBannerText}>DEVELOPMENT MODE</Text>
        </View>
      )}
      <FlatList
        ref={listRef}
        data={messages}
        keyExtractor={item => item.id.toString()}
        renderItem={({ item }) => (
          <View style={[
            styles.message,
            item.sender === 'user' ? styles.userMessage : styles.botMessage,
            item.isQuestion && styles.questionMessage
          ]}>
            <Text style={[
              styles.messageText,
              item.sender === 'user' ? styles.userMessageText : styles.botMessageText,
              item.isQuestion && styles.questionText
            ]}>
              {item.text}
            </Text>
            {item.options && item.sender === 'bot' && (
              <View style={styles.optionsContainer}>
                {item.options.map((option, index) => (
                  <TouchableOpacity
                    key={index}
                    style={styles.optionButton}
                    onPress={() => handleQuickResponse(option)}
                    disabled={loading}
                  >
                    <Text style={styles.optionText}>{option}</Text>
                  </TouchableOpacity>
                ))}
              </View>
            )}
          </View>
        )}
        onContentSizeChange={() => listRef.current?.scrollToEnd()}
        style={styles.messageList}
      />

      {showLoginPrompt && (
        <GoogleSignIn
          onSignIn={handleGoogleSignIn}
          onClose={handleSkipSignIn}
        />
      )}

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          value={input}
          onChangeText={setInput}
          placeholder="Type a message..."
          placeholderTextColor={colors.text.tertiary}
          onSubmitEditing={handleSendMessage}
          editable={!loading}
        />
        {loading && (
          <ActivityIndicator 
            style={styles.loader} 
            color={colors.primary} 
          />
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background.primary,
    paddingTop: 20
  },
