import React, { useState, useRef, useEffect } from 'react';
import { View, TextInput, FlatList, Text, StyleSheet, ActivityIndicator } from 'react-native';
import { colors } from '../../utils/colors';
import { sendMessage } from '../../services/ai';

export default function ChatUI({ userName }) {
  const isDev = process.env.NODE_ENV === 'development';
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const listRef = useRef(null);

  useEffect(() => {
    // Add welcome message
    setMessages([{
      id: Date.now(),
      text: `Welcome ${userName}! How can I help you today?`,
      sender: 'bot'
    }]);
  }, [userName]);

  useEffect(() => {
    if (isDev && userName === 'Test User') {
      console.log('Development mode: Test User connected');
    }
  }, [userName]);

  const handleSendMessage = async () => {
    if (!input.trim() || loading) return;

    try {
      setLoading(true);
      const userMessage = { id: Date.now(), text: input, sender: 'user' };
      setMessages(prev => [...prev, userMessage]);
      setInput('');

      const { response } = await sendMessage(input);

      const botMessage = {
        id: Date.now(),
        text: response,
        sender: 'bot'
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Chat error:', error);
      setMessages(prev => [...prev, {
        id: Date.now(),
        text: 'Sorry, something went wrong. Please try again.',
        sender: 'bot'
      }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      {isDev && (
        <View style={styles.devBanner}>
          <Text style={styles.devBannerText}>DEVELOPMENT MODE</Text>
        </View>
      )}
      <FlatList
        ref={listRef}
        data={messages}
        keyExtractor={item => item.id.toString()}
        renderItem={({ item }) => (
          <View style={[
            styles.message,
            item.sender === 'user' ? styles.userMessage : styles.botMessage
          ]}>
            <Text style={[
              styles.messageText,
              item.sender === 'user' ? styles.userMessageText : styles.botMessageText
            ]}>
              {item.text}
            </Text>
          </View>
        )}
        onContentSizeChange={() => listRef.current?.scrollToEnd()}
      />

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          value={input}
          onChangeText={setInput}
          placeholder="Type a message..."
          placeholderTextColor={colors.text.tertiary}
          onSubmitEditing={handleSendMessage}
          editable={!loading}
        />
        {loading && (
          <ActivityIndicator 
            style={styles.loader} 
            color={colors.primary} 
          />
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background.primary
  },
  message: {
    margin: 10,
    padding: 10,
    borderRadius: 20,
    maxWidth: '80%'
  },
  userMessage: {
    backgroundColor: colors.chatBubble.user,
    alignSelf: 'flex-end',
    marginLeft: '20%'
  },
  botMessage: {
    backgroundColor: colors.chatBubble.bot,
    alignSelf: 'flex-start',
    marginRight: '20%'
  },
  messageText: {
    fontSize: 16,
    lineHeight: 20
  },
  userMessageText: {
    color: colors.text.inverse
  },
  botMessageText: {
    color: colors.text.primary
  },
  inputContainer: {
    padding: 10,
    borderTopWidth: 1,
    borderTopColor: colors.background.tertiary,
    flexDirection: 'row',
    alignItems: 'center'
  },
  input: {
    flex: 1,
    backgroundColor: colors.background.secondary,
    padding: 12,
    borderRadius: 20,
    fontSize: 16,
    color: colors.text.primary
  },
  loader: {
    marginLeft: 10
  },
  devBanner: {
    backgroundColor: colors.background.tertiary,
    padding: 10,
    alignItems: 'center',
    justifyContent: 'center'
  },
  devBannerText: {
    color: colors.text.inverse,
    fontWeight: 'bold'
  }
});
