import React, { useState, useRef, useEffect } from 'react';
import { View, Text, TextInput, StyleSheet, TouchableOpacity, FlatList, ActivityIndicator } from 'react-native';
import { colors } from '../../utils/colors';
import { sendMessage } from '../../services/ai';
import GoogleSignIn from '../Auth/GoogleSignIn';

export default function ChatUI({ userName }) {
  const isDev = process.env.NODE_ENV === 'development';
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [connectionError, setConnectionError] = useState(false);
  const [questionCount, setQuestionCount] = useState(0);
  const [showLoginPrompt, setShowLoginPrompt] = useState(false);
  const [user, setUser] = useState(null);
  const listRef = useRef(null);

  useEffect(() => {
    // Add welcome message with initial question
    const welcomeMessage = {
      id: Date.now(),
      text: `Welcome ${userName}! I'm here to help you achieve your goals. What brings you here today?`,
      sender: 'bot',
      isQuestion: true
    };
    setMessages([welcomeMessage]);
  }, [userName]);

  useEffect(() => {
    if (isDev && userName === 'Test User') {
      console.log('Development mode: Test User connected');
    }
  }, [userName]);

  useEffect(() => {
    // Check backend connection
    const checkConnection = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/health');
        if (!response.ok) throw new Error('Backend unavailable');
        setConnectionError(false);
      } catch (error) {
        console.error('Connection check failed:', error);
        setConnectionError(true);
      }
    };

    checkConnection();
    const interval = setInterval(checkConnection, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleSendMessage = async () => {
    if (!input.trim() || loading) return;

    try {
      if (connectionError) {
        throw new Error('Cannot connect to chat service');
      }

      setLoading(true);
      const userMessage = { id: Date.now(), text: input, sender: 'user' };
      setMessages(prev => [...prev, userMessage]);
      setInput('');

      const result = await sendMessage(input);

      if (!result || !result.text) {
        throw new Error('Empty response from server');
      }

      const botMessage = {
        id: Date.now(),
        text: result.text,
        sender: 'bot',
        isQuestion: typeof result.text === 'string' && 
          (result.text.includes('?') || result.text.includes("I'd like to understand")),
        options: result.options || []
      };

      setMessages(prev => [...prev, botMessage]);

      // Increment question count if this was a question
      if (botMessage.isQuestion) {
        setQuestionCount(prev => prev + 1);
      }
    } catch (error) {
      console.error('Chat error:', error);
      setMessages(prev => [...prev, {
        id: Date.now(),
        text: connectionError ? 
          'Cannot connect to chat service. Please check your connection.' :
          'I apologize for the confusion. Let me ask you something else.',
        sender: 'bot'
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleQuickResponse = async (response) => {
    if (loading) return;

    try {
      setLoading(true);
      const userMessage = { id: Date.now(), text: response, sender: 'user' };
      setMessages(prev => [...prev, userMessage]);

      const result = await sendMessage(response);

      const botMessage = {
        id: Date.now(),
        text: result.text,
        sender: 'bot',
        isQuestion: typeof result.text === 'string' && 
          (result.text.includes('?') || result.text.includes("I'd like to understand")),
        options: result.options || []
      };

      setMessages(prev => [...prev, botMessage]);

      // Increment question count and check for login prompt
      if (botMessage.isQuestion) {
        const newCount = questionCount + 1;
        setQuestionCount(newCount);
        if (newCount >= 5 && !showLoginPrompt) {
          setShowLoginPrompt(true);
        }
      }
    } catch (error) {
      console.error('Quick response error:', error);
      setMessages(prev => [...prev, {
        id: Date.now(),
        text: 'I apologize, but I had trouble processing that. Let me ask something else.',
        sender: 'bot'
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    // TODO: Implement Google OAuth
    console.log('Google login clicked');
  };

  const handleGoogleSignIn = (userData) => {
    setUser(userData);
    setShowLoginPrompt(false);
    // TODO: Save conversation history
  };

  const handleSkipSignIn = () => {
    setShowLoginPrompt(false);
  };

  return (
    <View style={styles.container}>
      {isDev && (
        <View style={styles.devBanner}>
          <Text style={styles.devBannerText}>DEVELOPMENT MODE</Text>
        </View>
      )}
      <FlatList
        ref={listRef}
        data={messages}
        keyExtractor={item => item.id.toString()}
        renderItem={({ item }) => (
          <View style={[
            styles.message,
            item.sender === 'user' ? styles.userMessage : styles.botMessage,
            item.isQuestion && styles.questionMessage
          ]}>
            <Text style={[
              styles.messageText,
              item.sender === 'user' ? styles.userMessageText : styles.botMessageText,
              item.isQuestion && styles.questionText
            ]}>
              {item.text}
            </Text>
            {item.options && item.sender === 'bot' && (
              <View style={styles.optionsContainer}>
                {item.options.map((option, index) => (
                  <TouchableOpacity
                    key={index}
                    style={styles.optionButton}
                    onPress={() => handleQuickResponse(option)}
                    disabled={loading}
                  >
                    <Text style={styles.optionText}>{option}</Text>
                  </TouchableOpacity>
                ))}
              </View>
            )}
          </View>
        )}
        onContentSizeChange={() => listRef.current?.scrollToEnd()}
        style={styles.messageList}
      />

      {showLoginPrompt && (
        <GoogleSignIn
          onSignIn={handleGoogleSignIn}
          onClose={handleSkipSignIn}
        />
      )}

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          value={input}
          onChangeText={setInput}
          placeholder="Type a message..."
          placeholderTextColor={colors.text.tertiary}
          onSubmitEditing={handleSendMessage}
          editable={!loading}
        />
        {loading && (
          <ActivityIndicator 
            style={styles.loader} 
            color={colors.primary} 
          />
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background.primary,
    paddingTop: 20
  },
  message: {
    margin: 10,
    padding: 10,
    borderRadius: 20,
    maxWidth: '80%'
  },
  userMessage: {
    backgroundColor: colors.chatBubble.user,
    alignSelf: 'flex-end',
    marginLeft: '20%'
  },
  botMessage: {
    backgroundColor: colors.chatBubble.bot,
    alignSelf: 'flex-start',
    marginRight: '20%'
  },
  messageText: {
    fontSize: 16,
    lineHeight: 20
  },
  userMessageText: {
    color: colors.text.inverse
  },
  botMessageText: {
    color: colors.text.primary
  },
  inputContainer: {
    padding: 10,
    borderTopWidth: 1,
    borderTopColor: colors.background.tertiary,
    flexDirection: 'row',
    alignItems: 'center'
  },
  input: {
    flex: 1,
    backgroundColor: colors.background.secondary,
    padding: 12,
    borderRadius: 20,
    fontSize: 16,
    color: colors.text.primary
  },
  loader: {
    marginLeft: 10
  },
  devBanner: {
    backgroundColor: colors.background.tertiary,
    padding: 10,
    alignItems: 'center',
    justifyContent: 'center'
  },
  devBannerText: {
    color: colors.text.inverse,
    fontWeight: 'bold'
  },
  questionMessage: {
    backgroundColor: colors.chatBubble.system,
    borderWidth: 1,
    borderColor: colors.primary
  },
  questionText: {
    fontWeight: '500',
    color: colors.text.primary
  },
  optionsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'flex-start',
    marginTop: 10,
    gap: 8
  },
  optionButton: {
    backgroundColor: colors.background.secondary,
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 16,
    borderWidth: 1,
    borderColor: colors.primary
  },
  optionText: {
    color: colors.text.primary,
    fontSize: 14
  },
  loginPrompt: {
    backgroundColor: colors.background.secondary,
    padding: 16,
    margin: 16,
    borderRadius: 8,
    alignItems: 'center'
  },
  loginText: {
    color: colors.text.primary,
    marginBottom: 12,
    textAlign: 'center'
  },
  googleButton: {
    backgroundColor: '#4285F4',
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 4
  },
  googleButtonText: {
    color: colors.text.inverse,
    fontWeight: 'bold'
  },
  messageList: {
    flex: 1,
    width: '100%'
  }
});
